import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import styled from '../css/PirateStyles.module.css'
import Like from './Like';

const Pirate = (props) => {
    const [attributes,setAttributes] = useState(props.pirate.attributes)
    const [pirate,setPirate] = useState({})

    const addLikeHandler = (amount) =>{
        setPirate({
            ...pirate,
            likes: pirate.likes+1
        })

        axios.patch(`http://localhost:8000/api/edit/pirate/${props.id}`,pirate).then(response=>{
            console.log(response.data.pirate)

        })
    }
    useEffect(() => {
        axios.get(`http://localhost:8000/api/pirate/${props.id}`).then(response=>{
            console.log(response.data.pirate)
            setPirate(response.data.pirate);
        })
    }, []);


    return(
        <div className={styled.pirateBox}>            
            <img className={styled.pirateImg} src={props.pirate.image} alt={props.pirate.name} />
            <Link to={`/pirate/${props.pirate._id}`} className={styled.name}>{props.pirate.name}</Link>
            <p className={styled.crew}>{props.pirate.crew}</p>
            <ul className={styled.attrList}>
                <li><strong>Attributes: </strong></li>
            {
                attributes.map((item,i) => {
                    return <li key={i}>{item}, </li>
                })
            }
            </ul>
            <p className={styled.treasure}><strong>Treasure:</strong> {props.pirate.treasure}</p>
            {
                props.pirate.crew === "Captain" ? <p className={styled.likes} onClick={() => addLikeHandler() }><strong>Votes for Best Captain:</strong> {pirate.likes}</p> : ""
            }

        </div>
    )
}

export default Pirate;
