import React, { useState } from 'react';

const WelcomeForm = props => {
    var errorSize = Object.keys(props.dbError).length;

    const lengths = {
        name: 3,
        crew: 1
    }

    const onChangeHandler = (event) => {
        console.log(event.target.name)
        props.setForm({...props.form,[event.target.name]: event.target.value})
        console.log(event.target.value.length)
        if(event.target.name in props.error){
            if(event.target.value.length >= lengths[event.target.name]){
                props.setError({...props.error,[event.target.name]:true})
            }else{
                props.setError({...props.error,[event.target.name]:false})
            }
        }
    }

    const onSubmitHandler = (event) =>{
        console.log("hello")
    }

    return(
        <>
            <form onSubmit={onSubmitHandler} >
                <div className="errWrp">
                    {
                        errorSize > 1 ? <><h4>Entries Required: </h4> {Object.keys(props.dbError).join(', ')}</> : ""
                    }
                </div>

                <div>
                    <label htmlFor="firstName">First Name: </label>
                    <input type="text"  name="firstName" value={form.firstName} placeholder="First Name" onChange={onChangeHandler} />
                    {
                        props.error.firstName ? "" : <span>Please enter a first name</span>
                    }
                </div>

                <div>
                    <label htmlFor="lastName">Last Name: </label>
                    <input type="text"  name="lastName" value={form.lastName} placeholder="Last Name" onChange={onChangeHandler} />
                    {
                        props.error.firstName ? "" : <span>Please enter a first name</span>
                    }
                </div>

                <div>
                    <label htmlFor="email">Email: </label>
                    <input type="text"  name="email" value={form.email} placeholder="Email" onChange={onChangeHandler} />
                    {
                        props.error.email ? "" : <span>Please enter an email</span>
                    }
                </div>

                <div>
                    <label htmlFor="password">Password: </label>
                    <input type="text"  name="password" value={form.password} placeholder="Password" onChange={onChangeHandler} />
                    {
                        props.error.password ? "" : <span>Please enter a password</span>
                    }
                </div>

                <div>
                    <label htmlFor="confirmPassword">Confirm Password: </label>
                    <input type="text"  name="confirmPassword" value={form.password} placeholder="Confirm Password" onChange={onChangeHandler} />
                    {
                        props.error.password ? "" : <span>Passwords must match</span>
                    }
                </div>


                {
                    Object.keys(props.error).every((item) => props.error[item]) ? <input type="submit" value="Create" className="submit" /> : <input type="submit" value="Create" disabled className="disabled" />
                }

            </form>
        </>
    )
}

export default WelcomeForm;
