import axios from 'axios';
import React, { useEffect, useState } from 'react';
import styled from '../css/LikesStyles.module.css'

const Like = (props) => {
    const [likes,setLikes] = useState({
        id: props.id,
        like: props.like})

    const addLikeHandler = (amount) =>{
        var copyLik = likes
        setLikes({...likes, like: copyLik.like +1})
        console.log(likes.like + 1)

    }
    useEffect(() => {
        axios.patch(`http://localhost:8000/api/edit/pirate/${props.id}`, likes)
        .then(res =>  { 
            console.log("Liked pirate")
        })
    }, [likes]);

    return(
        <p className={styled.likes} onClick={() => addLikeHandler(likes) }><strong>Votes for Best Captain:</strong> {likes.like}</p>
    );
}

export default Like;
