import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Pirate from '../components/Pirate';

const Home = (props) => {
    const [pirates,setPirates] = useState([])

    const _ = require("lodash"); 
    let gfg = _.sortBy(pirates, ['_v:', 'name']);


    useEffect(() => {
        axios.get(`http://localhost:8000/api/pirates`).then(response=>{
            setPirates(response.data.pirates);
        })
    }, []);


    return(
        <div>
            <h1>Pirates</h1>
            <Link to="/new" className="button-lg">Add a Pirate</Link>
            <div className="allPirates">
                {
                    gfg.map((item,i) => {
                        return 
                        {
                            props.pirate.crew === "Captain" ? <> <Pirate key={i} pirate={item}/><p className={styled.likes} onClick={() => addLikeHandler() }><strong>Votes for Best Captain:</strong> {pirate.likes}</p></> : ""
                        }
                    }) 
                }
            </div>
        </div>
    )
}

export default Home;
