        like: props.like})
    console.log(likes)
    const addLikeHandler = (amount) =>{
        var copyLiked = likes

        console.log(copyLiked)
        copyLikes(likes + 1 )
        setLikes(copyLikes)

        axios.patch(`http://localhost:8000/api/edit/pirate/${props.id}`, likes)
        .then(res =>  { 
            console.log("Liked pirate")
        })
