import axios from 'axios';
import React, { useState } from 'react';
import styled from '../css/LikesStyles.module.css'

const Like = (props) => {
    const [likes,setLikes] = useState({likes: props.like})

    const addLikeHandler = (amount) =>{
        setLikes({likes: likes+1})
        console.log(props.id)
        // copyLikes(likes + 1 )
        // setLikes(copyLikes)

        axios.patch(`http://localhost:8000/api/edit/pirate/${props.id}`, likes)
        .then(res =>  { 
            console.log("Liked pirate")
        })
    }

    return(
        <p className={styled.likes} onClick={() => addLikeHandler(likes) }><strong>Votes for Best Captain:</strong> {likes}</p>
    );
}

export default Like;
